+++ 
@@ -0,0 +1,634 @@
+// main screen
+.pagesideline {
+    background: #fff;
+    position: fixed;
+    width: 75px;
+    z-index: 13;
+    height: 100vh;
+    display: flex;
+    flex-direction: column;
+    justify-content: space-between;
+    align-items: center;
+    padding: 75px 5px 24px;
+
+    &.pagesideline__left {
+        left: 0;
+        top: 0;
+    }
+
+    &.pagesideline__right {
+        right: 0;
+        top: 0;
+    }
+}
+
+.locfilter {
+    list-style-type: none;
+    text-align: center;
+
+    li {
+        margin-bottom: 5px;
+        padding-bottom: 5px;
+        border-bottom: 1px solid hsla(0, 0%, 59%, .2);
+
+        a {
+            display: block;
+            padding: 3px 8px;
+            font-weight: 400;
+            font-size: 11px;
+            color: #64656a;
+            transition: 0.3s;
+
+            &:hover {
+                color: #000;
+            }
+        }
+
+        &.active {
+            a {
+                font-weight: 600;
+            }
+        }
+
+        &:last-child {
+            border-bottom: none;
+        }
+    }
+}
+
+.pagesideline__logo {
+
+    img,
+    svg {
+        max-width: 25px;
+        height: auto;
+    }
+}
+
+.pagesideline__soclist {
+    list-style-type: none;
+    display: block;
+    width: 100%;
+    text-align: center;
+
+    li {
+        margin-bottom: 5px;
+
+        &:last-child {
+            margin-bottom: 0;
+        }
+
+        a {
+            display: block;
+            padding: 8px;
+
+            svg,
+            img {
+                max-width: 20px;
+                transition: 0.3s;
+
+                g {
+                    transition: 0.5s;
+                }
+
+            }
+
+            &:hover {
+                svg g {
+                    fill: var(--theme-text-color);
+                }
+            }
+        }
+    }
+}
+
+.pagesideline__btn {
+    padding: 0;
+    outline: none;
+    background: transparent;
+    border: none;
+}
+
+
+.mainvideosection {
+    position: relative;
+    height: calc(100vh - 50px);
+    display: flex;
+    align-items: center;
+    justify-content: center;
+
+    video {
+        position: absolute;
+        left: 0;
+        top: 0;
+        vertical-align: top;
+        width: 100%;
+        height: 100%;
+        object-fit: cover;
+        object-position: 50% 50%;
+    }
+}
+
+.mainvideosection__centerbox {
+    z-index: 1;
+    width: 100%;
+    text-align: center;
+    max-width: calc(100vw - 200px);
+
+    .videologo {
+        path {
+            fill: #fff;
+        }
+
+        img,
+        svg {
+            max-width: 100%;
+        }
+    }
+
+    .videodescr {
+        color: #fff;
+        font-size: 16px;
+        letter-spacing: .5px;
+
+    }
+}
+
+.scrollnext {
+    position: absolute;
+    bottom: 40px;
+    outline: none;
+    cursor: pointer;
+
+    img {
+        width: 9px;
+    }
+}
+
+// filter
+.filtersection {
+    padding: 50px 0;
+    background-color: hsla(240, 4%, 95%, .85);
+    text-align: center;
+
+    .select2-container {
+        width: 100% !important;
+    }
+
+    .select2-container--default .select2-selection--single {
+        border: none;
+        border-radius: 0;
+    }
+
+    .select2-container .select2-selection--single {
+        height: 40px;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__rendered {
+        line-height: 40px;
+        font-size: 13px;
+        color: #3c3c3c;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__arrow {
+        height: 38px;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__placeholder {
+        color: #3c3c3c;
+    }
+
+    form {
+        margin-bottom: 40px;
+    }
+
+    .text-center a {
+        font-size: 13px;
+        letter-spacing: .54px;
+        color: #64656a;
+        transition: 0.3s;
+        position: relative;
+
+        &::after {
+            content: '';
+            display: block;
+            position: absolute;
+            left: 0;
+            right: 0;
+            margin: 0 auto;
+            bottom: -2px;
+            width: 100%;
+            height: 1px;
+            background: #64656a;
+            transition: 0.3s;
+
+        }
+
+        &:hover {
+            color: #000;
+
+            &::after {
+                background: #000;
+                width: 0;
+            }
+        }
+    }
+
+
+}
+
+.filtersection__form {
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    flex-wrap: wrap;
+}
+
+.filtersection__box {
+    margin: 0 7px;
+    width: 100%;
+    max-width: 146px;
+    flex: 0 0 146px;
+
+    &.fblarge {
+        max-width: 270px;
+        flex: 0 0 270px;
+    }
+
+    input {
+        width: 100%;
+        height: 40px;
+        border: none;
+        padding-left: 15px;
+        font-size: 13px;
+        letter-spacing: .57px;
+        color: #3c3c3c;
+        font-family: 'Loew';
+        font-weight: 400;
+
+        &::placeholder {
+            color: #3c3c3c;
+        }
+    }
+}
+
+// property-blocks
+
+.property-section {
+    padding: 50px 0;
+
+    .property-section__sort {
+        display: flex;
+        align-items: center;
+        justify-content: flex-end;
+        max-width: 1290px;
+        margin: 0 auto 50px;
+        padding: 0 25px;
+    }
+
+    .title {
+        text-transform: uppercase;
+        font-size: 12px;
+        color: #aaa;
+        margin-right: 12px;
+        display: inline-block;
+        letter-spacing: .5px;
+        vertical-align: middle;
+    }
+
+    .select2-container {
+        width: 160px !important;
+    }
+
+    .select2-container--default .select2-selection--single {
+        // border: none;
+        border-radius: 0;
+    }
+
+    .select2-container .select2-selection--single {
+        height: 40px;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__rendered {
+        line-height: 40px;
+        font-size: 13px;
+        color: #3c3c3c;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__arrow {
+        height: 38px;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__placeholder {
+        color: #3c3c3c;
+    }
+
+    .gnrbtn {
+        margin-top: 40px;
+    }
+}
+
+.select2-results__option--selectable {
+    font-size: 13px;
+}
+
+.select2-container--default .select2-results__option--highlighted.select2-results__option--selectable {
+    background-color: #64656a;
+}
+
+.select2-dropdown {
+    top: 3px;
+    border: 1px solid #aaaaaa8b !important;
+    border-radius: 3px !important;
+}
+
+.select2-container--default .select2-results>.select2-results__options {
+    &::-webkit-scrollbar {
+        width: 6px;
+    }
+
+    &::-webkit-scrollbar-track {}
+
+    &::-webkit-scrollbar-thumb {
+        background-color: darkgrey;
+        border-radius: 10px;
+    }
+}
+
+.propertyboxes {
+    max-width: 1290px;
+    margin: 0 auto;
+    padding: 0 25px;
+
+    .propertybox {
+        width: 100%;
+        max-width: 25%;
+        flex: 0 0 25%;
+        margin-bottom: 24px;
+
+        a {
+            display: block;
+            height: 255px;
+            position: relative;
+            display: flex;
+            align-items: center;
+            justify-content: center;
+
+            &::after {
+                content: '';
+                display: block;
+                position: absolute;
+                width: 100%;
+                height: 100%;
+                left: 0;
+                top: 0;
+                opacity: 0;
+                transition: 0.3s;
+                background: transparent;
+            }
+
+            &:hover {
+                &::after {
+                    background: rgba(#000, 0.4);
+                    opacity: 1;
+                }
+
+                ul {
+                    opacity: 1;
+                    transform: translateY(0);
+                }
+
+            }
+        }
+
+        &:nth-child(6n+1),
+        &:nth-child(6n) {
+            max-width: 50%;
+            flex: 0 0 50%;
+        }
+
+        .description {
+            position: relative;
+            z-index: 1;
+            width: 100%;
+            padding: 0 10px;
+
+            h6 {
+                text-transform: uppercase;
+                letter-spacing: .62px;
+                font-size: 16px;
+                font-weight: 600;
+                color: #fff;
+                display: -webkit-box;
+                -webkit-line-clamp: 2;
+                -webkit-box-orient: vertical;
+                overflow: hidden;
+                text-align: center;
+            }
+
+            ul {
+                list-style-type: none;
+                text-align: center;
+                position: absolute;
+                width: 100%;
+                top: calc(100% + 30px);
+                opacity: 0;
+                transform: translateY(20px);
+                transition: 0.3s;
+
+                li {
+                    display: inline-block;
+                    vertical-align: middle;
+                    text-transform: uppercase;
+                    font-size: 13px;
+                    letter-spacing: .65px;
+                    margin-right: 14px;
+                    color: #fff;
+
+                    &:last-child {
+                        margin-right: 0;
+                    }
+                }
+            }
+        }
+
+    }
+
+}
+
+.propertybox__pic {
+    position: absolute;
+    left: 0;
+    top: 0;
+    object-fit: cover;
+    object-position: 50% 50%;
+    width: 100%;
+    height: 100%;
+}
+
+.filterbtn {
+    display: none;
+}
+
+@media(max-width:1440.8px) {
+    .propertyboxes .propertybox a {
+        height: 240px;
+    }
+
+    .propertyboxes .propertybox .description ul {
+        top: calc(100% + 10px);
+    }
+}
+
+@media(max-width:1199.8px) {
+    .filtersection__box {
+        margin-bottom: 15px;
+    }
+
+    .mainvideosection__centerbox .videologo img,
+    .mainvideosection__centerbox .videologo svg {
+        max-width: 480px;
+        width: 100%;
+    }
+}
+
+@media(max-width:991.8px) {
+
+    .pagesideline.pagesideline__right,
+    .pagesideline.pagesideline__left {
+        display: none;
+    }
+
+    .mainvideosection {
+        height: 100vh;
+    }
+
+    .filtersection__box.fblarge {
+        order: 1;
+        max-width: 100%;
+        flex: 0 0 100%;
+        display: block;
+    }
+
+    .filtersection__box {
+        order: 2;
+        max-width: 31%;
+        flex: 0 0 31%;
+        margin: 0 0 15px 0;
+        display: none;
+    }
+
+    .filtersection__form {
+        justify-content: space-between;
+
+        &.active {
+            .filtersection__box {
+                display: block;
+            }
+        }
+    }
+
+    .containersect {
+        padding-left: 25px;
+        padding-right: 25px;
+    }
+
+    .filterbtn {
+        margin-top: 15px;
+        display: inline-flex;
+        align-items: center;
+        padding: 5px 0;
+        font-size: 14px;
+        letter-spacing: .5px;
+        order: 3;
+
+        img {
+            margin-right: 10px;
+            width: 22px;
+        }
+    }
+
+    .filtersection form {
+        margin-bottom: 20px;
+    }
+
+    .propertyboxes .propertybox a {
+        height: 400px;
+    }
+
+    .propertyboxes .propertybox .description h6 {
+        font-size: 20px;
+    }
+
+    .propertyboxes {
+        padding-left: 0;
+        padding-right: 0;
+    }
+
+
+
+
+
+
+
+    .propertyboxes .propertybox:nth-child(6n),
+    .propertyboxes .propertybox:nth-child(6n+1),
+    .propertyboxes .propertybox {
+        max-width: 100%;
+        flex: 0 0 100%;
+    }
+
+
+
+
+
+
+
+
+
+
+}
+
+@media(max-width:767.8px) {
+    .propertyboxes .propertybox a {
+        height: 300px;
+    }
+}
+
+@media(max-width:575.8px) {
+    .filtersection {
+        padding: 40px 0;
+    }
+
+    .filterbtn {
+        font-size: 12px;
+        margin-top: 5px;
+    }
+
+    .filtersection .text-center a {
+        font-size: 12px;
+    }
+
+    .property-section .property-section__sort {
+        padding: 0;
+    }
+
+    .property-section .property-section__sort {
+        margin-bottom: 30px;
+    }
+
+    .propertyboxes .propertybox a {
+        height: 250px;
+    }
+
+    .propertyboxes .propertybox .description h6 {
+        font-size: 18px;
+    }
+
+}
+
+
+
+
+
+// letter-spacing: .57px;
