+// main screen
+.pagesideline {
+    background: #fff;
+    position: fixed;
+    width: 75px;
+    z-index: 13;
+    height: 100vh;
+    display: flex;
+    flex-direction: column;
+    justify-content: space-between;
+    align-items: center;
+    padding: 75px 5px 24px;
+
+    &.pagesideline__left {
+        left: 0;
+        top: 0;
+    }
+
+    &.pagesideline__right {
+        right: 0;
+        top: 0;
+    }
+}
+
+.locfilter {
+    list-style-type: none;
+    text-align: center;
+
+    li {
+        margin-bottom: 5px;
+        padding-bottom: 5px;
+        border-bottom: 1px solid hsla(0, 0%, 59%, .2);
+
+        a {
+            display: block;
+            padding: 3px 8px;
+            font-weight: 400;
+            font-size: 11px;
+            color: #64656a;
+            transition: 0.3s;
+
+            &:hover {
+                color: #000;
+            }
+        }
+
+        &.active {
+            a {
+                font-weight: 600;
+            }
+        }
+
+        &:last-child {
+            border-bottom: none;
+        }
+    }
+}
+
+.pagesideline__logo {
+
+    img,
+    svg {
+        max-width: 25px;
+        height: auto;
+    }
+}
+
+.pagesideline__soclist {
+    list-style-type: none;
+    display: block;
+    width: 100%;
+    text-align: center;
+
+    li {
+        margin-bottom: 5px;
+
+        &:last-child {
+            margin-bottom: 0;
+        }
+
+        a {
+            display: block;
+            padding: 8px;
+
+            svg,
+            img {
+                max-width: 20px;
+                transition: 0.3s;
+
+                g {
+                    transition: 0.5s;
+                }
+
+            }
+
+            &:hover {
+                svg g {
+                    fill: var(--theme-text-color);
+                }
+            }
+        }
+    }
+}
+
+.pagesideline__btn {
+    padding: 0;
+    outline: none;
+    background: transparent;
+    border: none;
+}
+
+
+.mainvideosection {
+    position: relative;
+    height: calc(100vh - 50px);
+    display: flex;
+    align-items: center;
+    justify-content: center;
+
+    video {
+        position: absolute;
+        left: 0;
+        top: 0;
+        vertical-align: top;
+        width: 100%;
+        height: 100%;
+        object-fit: cover;
+        object-position: 50% 50%;
+    }
+}
+
+.mainvideosection__centerbox {
+    z-index: 1;
+    width: 100%;
+    text-align: center;
+    max-width: calc(100vw - 200px);
+
+    .videologo {
+        path {
+            fill: #fff;
+        }
+
+        img,
+        svg {
+            max-width: 100%;
+        }
+    }
+
+    .videodescr {
+        color: #fff;
+        font-size: 16px;
+        letter-spacing: .5px;
+
+    }
+}
+
+.scrollnext {
+    position: absolute;
+    bottom: 40px;
+    outline: none;
+    cursor: pointer;
+
+    img {
+        width: 9px;
+    }
+}
+
+// filter
+.filtersection {
+    padding: 50px 0;
+    background-color: hsla(240, 4%, 95%, .85);
+    text-align: center;
+
+    .select2-container {
+        width: 100% !important;
+    }
+
+    .select2-container--default .select2-selection--single {
+        border: none;
+        border-radius: 0;
+    }
+
+    .select2-container .select2-selection--single {
+        height: 40px;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__rendered {
+        line-height: 40px;
+        font-size: 13px;
+        color: #3c3c3c;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__arrow {
+        height: 38px;
+    }
+
+    .select2-container--default .select2-selection--single .select2-selection__placeholder {
+        color: #3c3c3c;
+    }
+
+    form {
+        margin-bottom: 40px;
+    }
+
+    .text-center a {
+        font-size: 13px;
+        letter-spacing: .54px;
+        color: #64656a;
+        transition: 0.3s;
+        position: relative;
+
+        &::after {
+            content: '';
+            display: block;
+            position: absolute;
+            left: 0;
+            right: 0;
+            margin: 0 auto;
+            bottom: -2px;
+            width: 100%;
+            height: 1px;
+            background: #64656a;
+            transition: 0.3s;
+
+        }
+
+        &:hover {
+            color: #000;
+
+            &::after {
+                background: #000;
+                width: 0;
+            }
+        }
+    }
+
+
+}
+
+.filtersection__form {
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    flex-wrap: wrap;
+}
+
+.filtersection__box {
+    margin: 0 7px;
+    width: 100%;
+    max-width: 146px;
+    flex: 0 0 146px;
+
+    &.fblarge {
+        max-width: 270px;
+        flex: 0 0 270px;
+    }
+
+    input {
+        width: 100%;
+        height: 40px;
+        border: none;
+        padding-left: 15px;
+        font-size: 13px;
+        letter-spacing: .57px;
+        color: #3c3c3c;
+        font-family: 'Loew';
+        font-weight: 400;
+
+        &::placeholder {
+            color: #3c3c3c;
+        }
+    }
+}
+
+// property-blocks
+
+.property-section {
+    padding: 50px 0;
+
+    .property-section__sort {
+        display: flex;
