+        margin: 0 auto 50px;
+        padding: 0 25px;
+    }
+
+    .title {
+        text-transform: uppercase;
+        font-size: 12px;
+        color: #aaa;
+        margin-right: 12px;
+        display: inline-block;
+        letter-spacing: .5px;
+        vertical-align: middle;
+    }
+
+    .select2-container {
+        width: 160px !important;
+    }
+
+    .select2-container--default .select2-selection--single {
+        // border: none;
