+// main screen
+.pagesideline {
+    background: #fff;
+    position: fixed;
+    width: 75px;
+    z-index: 13;
+    height: 100vh;
+    display: flex;
+    flex-direction: column;
+    justify-content: space-between;
+    align-items: center;
+    padding: 75px 5px 24px;
+
+    &.pagesideline__left {
+        left: 0;
+        top: 0;
+    }
+
+    &.pagesideline__right {
+        right: 0;
+        top: 0;
+    }
+}
+
+.locfilter {
+    list-style-type: none;
+    text-align: center;
+
+    li {
+        margin-bottom: 5px;
+        padding-bottom: 5px;
+        border-bottom: 1px solid hsla(0, 0%, 59%, .2);
+
+        a {
+            display: block;
+            padding: 3px 8px;
+            font-weight: 400;
+            font-size: 11px;
+            color: #64656a;
+            transition: 0.3s;
+
+            &:hover {
+                color: #000;
+            }
+        }
+
+        &.active {
+            a {
+                font-weight: 600;
+            }
+        }
+
+        &:last-child {
+            border-bottom: none;
+        }
+    }
+}
+
+.pagesideline__logo {
+
+    img,
+    svg {
+        max-width: 25px;
+        height: auto;
+    }
+}
+
+.pagesideline__soclist {
+    list-style-type: none;
+    display: block;
+    width: 100%;
+    text-align: center;
+
+    li {
+        margin-bottom: 5px;
+
+        &:last-child {
+            margin-bottom: 0;
+        }
+
+        a {
+            display: block;
+            padding: 8px;
+
+            svg,
+            img {
+                max-width: 20px;
+                transition: 0.3s;
+
+                g {
+                    transition: 0.5s;
+                }
+
+            }
+
+            &:hover {
+                svg g {
+                    fill: var(--theme-text-color);
+                }
+            }
+        }
+    }
+}
+
+.pagesideline__btn {
+    padding: 0;
+    outline: none;
+    background: transparent;
+    border: none;
+}
+
+
+.mainvideosection {
