+.aboutsection {
+    padding: 75px 0;
+}
+
+.aboutsection__content {
+    background: #f1f1f2;
+    padding: 80px 120px;
+    text-align: center;
+
+    p {
+        color: #3c3c3c;
+        font-size: 16px;
+        line-height: 26px;
+        margin-bottom: 20px;
+    }
+
+    .abgroup {
+        margin-top: 50px;
+    }
+}
+
+.aboutbox {
+    .imgwrap {
+        margin-bottom: 20px;
+
+        img {
+            width: 100%;
+        }
+
+    }
+
+    .description {
+        p {
+            font-size: 14px;
+            line-height: 150%;
+            margin: 0;
+        }
+
+        .title {
+            text-transform: uppercase;
+            font-size: 18px;
+            color: #3c3c3c;
+            font-weight: 600;
+            margin-bottom: 15px;
+        }
+    }
+}
+
+.darktheme {
+    transition: 0.5s;
+    background: var(--theme-background-color);
+
+    .teamsection__title {
+        color: #fff;
+    }
+}
+
+.teamsection__title {
+    text-transform: uppercase;
+    font-size: 18px;
+    font-weight: 600;
+    color: #64656a;
+    margin-bottom: 30px;
+}
+
+.teambox {
+    height: 300px;
+    position: relative;
+    overflow: hidden;
+    margin-bottom: 25px;
+
+    img {
+        width: 100%;
+        height: 100%;
+        object-fit: cover;
+        object-position: 50% 50%;
+        filter: grayscale(1);
+        transition: 0.3s;
+    }
+
+    .teambox__info {
+        position: absolute;
+        left: 0;
+        bottom: 0;
+        width: 100%;
+        padding: 0 20px 10px 10px;
+        text-align: right;
+        transition: 0.3s;
+        transform: translateY(10px);
+
+        p {
+            color: #fff;
+            text-transform: uppercase;
+            font-size: 12px;
+            opacity: 0;
+            transition: 0.3s;
+            text-overflow: ellipsis;
+            overflow: hidden;
+            white-space: nowrap;
+        }
+
+        .name {
+            text-transform: uppercase;
+            color: #fff;
+            font-weight: 600;
+            font-size: 16px;
+            opacity: 1;
+        }
+    }
+
+    &:hover {
+        .teambox__info {
+            transform: translateY(0);
+
+            p {
+                opacity: 1;
+            }
+        }
+
+        img {
+            filter: grayscale(0);
+        }
+    }
+}
+
+.teamgroup {
+    margin-bottom: 60px;
+}
+
+
+// ====
+.teamopen {
+    .smcontainer {
+        padding: 110px;
+    }
+
+    .teamopen__left {
+        h1 {
+            color: #3c3c3c;
+            font-weight: 600;
+            margin-bottom: 5px;
+            font-size: 30px;
+            text-transform: uppercase;
+        }
+
+        .subtitle {
+            text-transform: uppercase;
+            font-size: 18px;
+            margin-bottom: 30px;
+        }
+
+        a {
+            font-size: 14px;
+            color: var(--theme-text-color);
+            position: relative;
+            padding-left: 25px;
+            display: inline-block;
+            margin-bottom: 30px;
+
+            &::after {
+                content: "";
+                display: inline-block;
+                width: 16px;
+                height: 20px;
+                position: absolute;
+                left: 0;
+                top: -1px;
+                background: url(../img/general/listarrow.png) no-repeat center / 14px;
+            }
+        }
+
+        p {
+            font-size: 14px;
+            color: #64656a;
+            margin-bottom: 30px;
+        }
+    }
+
+    .teamopen__right {
+        text-align: right;
+
+        a {
+            font-size: 14px;
+            color: var(--theme-text-color);
+            position: relative;
+            padding-left: 22px;
+            display: inline-block;
+            margin-bottom: 10px;
+
+            &::after {
+                content: "";
+                display: inline-block;
+                width: 16px;
+                height: 20px;
+                position: absolute;
+                left: 0;
+                top: 0px;
+                background: url(../img/general/listarrow.png) no-repeat center / 14px;
+                transform: rotate(180deg);
+            }
+        }
+    }
+}
